INSERT INTO public.ethnicities (id, name, origin, physical_traits, cultural_traits, traditions, language, religion, social_structure, history, geography, "values", customs, genre, user_id, created_at, article_content, notebook_id, image_url, image_caption) VALUES ('d8f0d258-034d-41b3-b789-338a577562e4', 'Vaultmen', NULL, NULL, '[p]The Vaultmen represent humanity''s attempt to preserve civilization through scientific rationality and technological control—but at the cost of traditional human values of compassion, community, and individual dignity. They are simultaneously humanity''s potential salvation and a cautionary tale about survival at any cost.[/p]
[p][/p]
[h4|87284953-a283-403d-9d26-7a83965c804d]RELATIONSHIPS WITH OTHER FACTIONS[/h4]
[hr]
[p][/p]
[ul]
- [b]Hostile to Rootbound[/b]: Viewed as contaminated collaborators with the Verdancy
- [b]Exploitative of Saltfolk[/b]: Trade partners but considered inferior and expendable
- [b]Suspicious of All Outsiders[/b]: Anyone not Archive-affiliated is a potential contamination risk
- [b]Frostborne: [/b]In awe at their ability to survive at high altitudes with limited technology, but not seen as a threat.
[/ul]
[p][/p]
