INSERT INTO public.ethnicities (id, name, origin, physical_traits, cultural_traits, traditions, language, religion, social_structure, history, geography, "values", customs, genre, user_id, created_at, article_content, notebook_id, image_url, image_caption) VALUES ('dc4d7b50-391c-42aa-afe9-1d887caa088b', 'Vaultmen', '', '', 'Inherited from the Past:



• Advanced pre-Tide technology and scientific knowledge
• Academic and military institutional structures
• Research methodologies and data preservation techniques
• Biotech labs and weapon prototypes
• The motto "Knowledge Survives. Even If We Don''t"




Modern Preservation Efforts: Aggressive data archiving, protection of research facilities, and recruitment of individuals with valuable knowledge or technical skills.', '{}', '• Standard Archive: A technical pidgin mixing English, scientific Latin, and military terminology.
• Clearance Codes: Numerical and alphabetic designation systems for security communication.
• Station Dialects: Regional variations based on specific Archive installations, incorporating local technical terminology and operational codes.', '', '', '', '', '{}', '{}', 'Fantasy', '33081217', '2025-10-07 16:59:01.280084', NULL, '0fe4e254-02d1-4a14-9046-6b3aefe17a89', '', '');


