INSERT INTO public.items (id, name, item_type, description, rarity, value, weight, properties, materials, history, abilities, requirements, crafting, genre, user_id, created_at, notebook_id, article_content, image_url, image_caption) VALUES ('a36e4b86-ecd8-4e78-8de1-eb603940f3d8', 'Gritstone Cakes', 'other', '[p]Gritstone cakes are the @[Saltfolk](Article:13c0c67e-37d3-41ed-94a8-2232e37843a0)''s durable, portable survival food. It is made using @[Mesquite Pinole](Article:c296703a-71dc-4a24-a6ef-dfa16a7562d6) and @[Cattails](Article:9ebbda24-3b62-4c44-a080-92868ff2b578) root flour that is mixed with just enough water to form a very stiff dough. It is shaped into thick, biscuit-like patties and baked on a hot, flat stone (gritstone) that is placed next to the fire. The result is a dense, dry, and incredibly durable cake that lasts for weeks without spoiling, making it the perfect travelling food for hunters, scouts, and[/p]
[p][/p]
[p][/p]
', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '33081217', '2025-10-07 11:59:46.481971', '0fe4e254-02d1-4a14-9046-6b3aefe17a89', NULL, NULL, NULL);
