INSERT INTO public.items (id, name, item_type, description, rarity, value, weight, properties, materials, history, abilities, requirements, crafting, genre, user_id, created_at, notebook_id, article_content, image_url, image_caption) VALUES ('b57e618c-b0b3-4fb7-9cb8-6b02fd015b66', 'Gritstone Cakes', 'Food', 'Gritstone cakes are the Saltfolk''s durable, portable survival food. It is made using Mesquite Pinole and Cattails root flour that is mixed with just enough water to form a very stiff dough. It is shaped into thick, biscuit-like patties and baked on a hot, flat stone (gritstone) that is placed next to the fire. The result is a dense, dry, and incredibly durable cake that lasts for weeks without spoiling, making it the perfect travelling food for hunters, scouts, and', '', '', '', '{}', '{}', '', '{}', '', '', 'Fantasy', '33081217', '2025-10-07 16:58:56.33146', '0fe4e254-02d1-4a14-9046-6b3aefe17a89', NULL, '', '');
INSERT INTO public.items (id, name, item_type, description, rarity, value, weight, properties, materials, history, abilities, requirements, crafting, genre, user_id, created_at, notebook_id, article_content, image_url, image_caption) VALUES ('c7f2980c-863f-4ba2-8911-c20bcbb2b25b', 'Honey Mesquite Tea', 'Consumable, Food / Drink', 'The leaves of the Honey Mesquite Tree are steeped in hot water to create a medicinal tea used to treat stomach ailments and fevers.', '', '', '', '{}', '{}', '', '{}', '', '', 'Fantasy', '33081217', '2025-10-07 16:58:56.530037', '0fe4e254-02d1-4a14-9046-6b3aefe17a89', NULL, '', '');
