INSERT INTO public.items (id, name, item_type, description, rarity, value, weight, properties, materials, history, abilities, requirements, crafting, genre, user_id, created_at, notebook_id, article_content, image_url, image_caption) VALUES ('b72654f3-9566-45c4-92ae-da90c41ccc7b', 'Oasis Harvest Loaf', 'Food', 'Oasis Harvest Loaf is the Saltfolk version of an Old World fruitcake that is a dense loaf combining all of their most valuable ingredients that are collected during the Oasis Harvest. The base of the dough is a mix of Mesquite Pinole and Cattails flour, enriched with Pinyon Nut Butter and sweetened with a generous amount of date paste. The loaf is studded with whole dried dates and chopped Desert Wolfberry berries. The loaf is slowly baked in the ashes of a fire pit. Eating it is the celebration of security, abundance, and a taste of wealth that sees Espera through the', '', '', '', '{}', '{}', '', '{}', '', '', 'Fantasy', '33081217', '2025-10-07 16:58:58.217655', '0fe4e254-02d1-4a14-9046-6b3aefe17a89', NULL, '', '');
INSERT INTO public.items (id, name, item_type, description, rarity, value, weight, properties, materials, history, abilities, requirements, crafting, genre, user_id, created_at, notebook_id, article_content, image_url, image_caption) VALUES ('5b2e9240-7171-4872-acfd-235582f2d8cc', 'Oasis Salad', 'Food', 'During the Oasis Harvest, the oasis salad provides a rare burst of fresh flavour to a diet that consists mostly of dried and roasted foods. During the annual journey to Furnace Creek for the Oasis Harvest, a salad of fresh, crisp Pickleweed tips and tender young Nopales are carefully de-spined and finely chopped. It is a crunchy, salt, and hydrating treat that is considered a luxury among the Saltfolk of Espera. The star ingredient of the dish are Tunas (prickly pear fruit) from the Prickly Pear Cactus. After carefully burning or scraping off the tiny, hair-like spines, the Saltfolk peel and dice the sweet, vibrant flesh to add a juicy, melon-like sweetness and stunning colour to the salad. The small, tangy, and slightly sweet red Wolfberries are also scattered throughout the salad like little jewels, adding a burst of coclour and a tart flavour to contrast the sweetness of the prickly pear. Desert Purslane, the low-growing succulent with fleshy, paddle-shaped leaves give an additional, slightly sour and lemony flavour to the salad, and a crisp, juicy texture. It adds moisture and a refreshing tang to the meal. Lastly, toasted Pinyon Nuts are prepared alongside the fresh ingredients by the fire. They are then slightly crushed and sprinkled over the top of the salad to add a rich, nutty flavour, a fatty mouthfeel, and delightfully crunchy texture.', '', '', '', '{}', '{}', '', '{}', '', '', 'Fantasy', '33081217', '2025-10-07 16:58:58.287869', '0fe4e254-02d1-4a14-9046-6b3aefe17a89', NULL, '', '');
INSERT INTO public.items (id, name, item_type, description, rarity, value, weight, properties, materials, history, abilities, requirements, crafting, genre, user_id, created_at, notebook_id, article_content, image_url, image_caption) VALUES ('cb46762c-f03b-4871-aef7-c39c1aaedaaf', 'Pinyon Nut Butter', 'Consumable, Food / Drink', 'The nuts of the Pinyon Pine Tree are ground into a rich, oily paste or butter—a nutrient-dense superfood that is easily transported back to Espera during a Piñonada.', '', '', '', '{}', '{}', '', '{}', '', '', 'Fantasy', '33081217', '2025-10-07 16:58:58.600235', '0fe4e254-02d1-4a14-9046-6b3aefe17a89', NULL, '', '');
