INSERT INTO public.items (id, name, item_type, description, rarity, value, weight, properties, materials, history, abilities, requirements, crafting, genre, user_id, created_at, notebook_id, article_content, image_url, image_caption) VALUES ('5d5f1853-bdc6-4dd2-9d2e-ee57937add75', 'Salted Porridge', 'Food', 'The most common daily meal in Espera is salted porridge. It''s simply, hydrating, and provides slow-burning energy throughout the day. It is made using Mesquite Pinole that is mixed with water and Pickleweed salt in a scavenged pot or carved stone bowl. It can be eaten cold as a simple gruel, or heated into a porridge by cooking it over a stove or fire. For a richer version, the Saltfolk add some mashed dates for sweetness and a spoonful of Pinyon Nut Butter for added fat and calories.', '', '', '', '{}', '{}', '', '{}', '', '', 'Fantasy', '33081217', '2025-10-07 16:58:59.28278', '0fe4e254-02d1-4a14-9046-6b3aefe17a89', NULL, '', '');
INSERT INTO public.items (id, name, item_type, description, rarity, value, weight, properties, materials, history, abilities, requirements, crafting, genre, user_id, created_at, notebook_id, article_content, image_url, image_caption) VALUES ('d6d7a9c9-1ae3-4b1b-9b82-a7692be57810', 'Sunstone Jerky', 'Food', 'Sunstone Jerky is the result of preserved meat of any type after a large, successful hunt. Thin strips of meat are coated in a paste made of Pickleweed salt and Mesquite Pinole. The sweetness of the mesquite acts as a slight cure and creates a crust on the meat. The strips of meat are then laid out on dark, flat rocks (sunstones) in the intense desert sun to dry completely. This creates a preserved, protein-rich jerky for lean times among Saltfolk in Espera.', '', '', '', '{}', '{}', '', '{}', '', '', 'Fantasy', '33081217', '2025-10-07 16:59:00.22151', '0fe4e254-02d1-4a14-9046-6b3aefe17a89', NULL, '', '');
