INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('a0792425-65e5-4260-8c75-9a2e4d116571', 'Kharis al-Mayyit', 'From Arabic ''kharis'' meaning silent/mute and ''al-mayyit'' meaning the dead one. In Middle Eastern folklore, ghouls were often described as silent predators who haunted graveyards and consumed the dead.', 'Arabic/Middle Eastern', 'ghoul', '', '33081217', '2025-10-09 23:22:29.664645', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('d2c1932e-2c6b-4525-a022-24e53bec7dd6', 'Vetala Ashtamukhi', 'From Sanskrit ''vetala'' referring to a ghoul-like spirit that inhabits corpses, and ''ashtamukhi'' meaning eight-faced. In Hindu mythology, vetalas are undead beings with supernatural knowledge.', 'Sanskrit/Hindu', 'ghoul', '', '33081217', '2025-10-09 23:22:29.75603', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('1711b9e9-beca-499c-93a6-9fd56b02271b', 'Draugar Gunnhild', 'From Old Norse ''draugar'' meaning undead revenant and ''Gunnhild'' meaning battle-strife. Draugr were corporeal undead in Norse mythology who possessed superhuman strength and an insatiable hunger.', 'Old Norse/Scandinavian', 'ghoul', '', '33081217', '2025-10-09 23:22:29.781195', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('bfc876f5-202c-4513-89fb-0e99cb44bd81', 'Jiangshi Wúmíng', 'From Chinese ''jiangshi'' meaning stiff corpse (hopping vampire/ghoul) and ''wúmíng'' meaning nameless. These creatures in Chinese folklore are reanimated corpses that hop due to rigor mortis.', 'Chinese', 'ghoul', '', '33081217', '2025-10-09 23:22:29.808645', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('a44296d8-1a77-4adf-811e-21b02e6ca44e', 'Rakshasa Bhakshaniya', 'From Sanskrit ''rakshasa'' meaning demon/ghoul and ''bhakshaniya'' meaning devourer. In Hindu mythology, rakshasas are shape-shifting demons known for consuming human flesh.', 'Sanskrit/Hindu', 'ghoul', '', '33081217', '2025-10-09 23:22:29.832899', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('d858173a-d894-4cf9-9487-4c4c91d5af2e', 'Ghul ibn Shaitan', 'From Arabic ''ghul'' the original word for ghoul meaning desert demon, and ''ibn Shaitan'' meaning son of Satan. The classical ghoul of Arabian folklore that shapeshifts and devours travelers in the desert.', 'Arabic/Arabian', 'ghoul', '', '33081217', '2025-10-09 23:22:29.857071', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('5fa4c92e-b5b4-4d78-9ba1-abef6151bc52', 'Kenji Nakamura', 'Kenji means ''intelligent second son'' in Japanese, combining ''ken'' (intelligent/wise) and ''ji'' (second son). Nakamura is a common Japanese surname meaning ''middle village'', indicating ancestral ties to a settlement between other communities.', 'Japanese', 'character', '', '33081217', '2025-10-10 15:02:31.653661', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('447a1289-b519-42d4-b605-73ea30d58f36', 'Astrid Eriksdottir', 'Astrid derives from Old Norse meaning ''divinely beautiful'' or ''divine star'', combining ''áss'' (god) and ''fríðr'' (beautiful). Eriksdottir follows traditional Norse patronymic naming, meaning ''daughter of Erik'', connecting her to her father''s lineage.', 'Old Norse/Scandinavian', 'character', '', '33081217', '2025-10-10 15:02:31.730869', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('dcdac5fd-29ed-4079-8d00-29a86f39af66', 'Amara Okafor', 'Amara means ''grace'' or ''eternal'' in Igbo culture, representing divine favor and lasting beauty. Okafor is an Igbo surname meaning ''born on Afor market day'', indicating the day of the week when the ancestor was born according to the traditional Igbo calendar.', 'Igbo (Nigerian)', 'character', '', '33081217', '2025-10-10 15:02:31.759181', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('eacb22e3-3085-47a6-8b11-34ca2c7a9170', 'Santiago Mendoza', 'Santiago honors Saint James (Santiago in Spanish), patron saint of Spain, combining ''santo'' (saint) and ''Iago'' (James). Mendoza is a Basque surname meaning ''cold mountain'', originally referring to someone from the mountainous Mendoza region.', 'Spanish/Basque', 'character', '', '33081217', '2025-10-10 15:02:31.784515', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('6d53c9ad-657f-42e8-bbab-874557dea2cb', 'Priya Sharma', 'Priya is a Sanskrit name meaning ''beloved'' or ''dear one'', often associated with the goddess Lakshmi. Sharma is a common Brahmin surname derived from Sanskrit meaning ''joy'' or ''protection'', traditionally indicating a priestly or scholarly family lineage.', 'Sanskrit/Indian', 'character', '', '33081217', '2025-10-10 15:02:31.810592', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('75d556ca-8026-4c1a-a2ba-e02de309099d', 'Seamus O''Brien', 'Seamus is the Irish form of James, meaning ''supplanter'' in Hebrew, but deeply rooted in Irish Catholic tradition. O''Brien means ''descendant of Brian'', honoring the legendary High King Brian Boru, with ''brian'' meaning ''noble'' or ''high'' in Gaelic.', 'Irish Gaelic', 'character', '', '33081217', '2025-10-10 15:02:31.83774', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('f4e45ca4-1486-4597-b473-cb92c9a48364', 'Nerida', 'From Greek ''Nereis'', meaning ''sea nymph'' or ''daughter of Nereus''. In Greek mythology, the Nereids were fifty sea nymphs who dwelt in the Mediterranean Sea, known for their beauty and benevolence toward sailors.', 'Greek', 'mermaid', '', '33081217', '2025-10-10 20:05:50.922536', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('addc08a3-72a9-4ce2-9742-04904a1ed30a', 'Muirenn', 'From Irish Gaelic, meaning ''sea white'' or ''born of the sea''. Composed of ''muir'' (sea) and ''finn'' (white/fair). In Irish folklore, this name was associated with water spirits and sea maidens.', 'Irish Gaelic', 'mermaid', '', '33081217', '2025-10-10 20:05:51.013969', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('5a726b8c-b672-43f9-b45f-176e0a227e2e', 'Thalassa', 'Ancient Greek personification of the sea itself, literally meaning ''sea'' or ''ocean''. Thalassa was the primordial goddess of the sea in Greek mythology, representing the Mediterranean Sea''s feminine spirit.', 'Ancient Greek', 'mermaid', '', '33081217', '2025-10-10 20:05:51.045226', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('1b92b906-b5f3-44d9-98ca-8f9fa7d1affa', 'Ran', 'Old Norse goddess of the sea, whose name means ''robbery'' or ''plunder'', reflecting the sea''s power to claim lives. Ran used her net to capture sailors who drowned, bringing them to her underwater hall.', 'Norse', 'mermaid', '', '33081217', '2025-10-10 20:05:51.100538', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('a24a99b1-fda1-4cec-b160-56fbf5b130ea', 'Melusine', 'From Old French folklore, possibly derived from ''Mere Lusigne'' meaning ''mother of the Lusignan dynasty''. Melusine was a legendary water spirit who could transform between human and serpentine forms, associated with springs and rivers.', 'French/Celtic', 'mermaid', '', '33081217', '2025-10-10 20:05:51.074484', NULL);
INSERT INTO public.names (id, name, meaning, origin, name_type, culture, user_id, created_at, notebook_id) VALUES ('7b5436cf-ff4e-41bf-944f-b0b28273607a', 'Yemoja', 'Yoruba orisha (deity) of the ocean, rivers, and motherhood. Her name means ''mother whose children are fish''. She is revered as the mother of all life and protector of women and children, particularly associated with the Ogun River.', 'Yoruba (West African)', 'mermaid', '', '33081217', '2025-10-10 20:05:51.126293', NULL);


