INSERT INTO public.ranks (id, name, rank_type, description, hierarchy, authority, responsibilities, privileges, insignia, requirements, organization_id, superior_ranks, subordinate_ranks, title_of_address, historical_origin, genre, notebook_id, user_id, created_at) VALUES ('21597c51-a854-4ae6-bcff-07c9f35dab90', 'Keeper', 'other', '[p]A Keeper is an elected or appointed official on the Council of Keepers who serves as the governmental liaison and coordinator for one of Espera''s essential survival domains. Unlike the specialized organizations (Jar Mothers, Hornbearers, etc.) that handle day-to-day operations, Keepers serve as the political representatives who bring each domain''s needs, resources, and expertise to the council table for community-wide decision making.[/p]
[p][/p]
[p]Each Keeper acts as both advocate and overseer for their designated area of survival, ensuring that resources are allocated fairly, that their domain''s concerns are heard in governance, and that the community''s laws and decisions support their specialized work.[/p]
[p][/p]
[p]Each Keeper serves a rotating term based on community trust and proven expertise in their domain. They meet regularly in the Keeper''s Den to make collective decisions about resource allocation, community law, trade agreements, and responses to threats. The Desert Keeper serves as the tiebreaker and final authority, but all Keepers have equal voice in deliberations.[/p]
[p][/p]
[p][/p]
', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '0fe4e254-02d1-4a14-9046-6b3aefe17a89', '33081217', '2025-10-07 13:55:11.397316');
