INSERT INTO public.rituals (id, name, ritual_type, description, purpose, participants, requirements, steps, duration, location, timing, components, effects, risks, variations, genre, user_id, created_at, notebook_id) VALUES ('5bd474a6-4006-4fe1-8380-38260ceca112', 'Piñonada', 'other', '[p]To utilize the @[Pinyon Pine Tree](Article:3f2060ba-a545-4653-90c0-3ae0d5b9e4c5), @[Espera](Article:62fc36c4-cd05-48a8-a4b6-76c99a69d091) undertakes a major, organized seasonal expedition—a "piñonada"—migrating from the extreme heat of the lowlands up into the cooler, higher elevations of the @[Panamint Mountains](Article:712aa320-e74b-4075-965f-5dd3fdb00e20) or @[Grapevine Mountains](Article:5279459c-43d6-4d67-959a-262171ef3fcf). This annual journey occurs in the late summer and early autumn, and is critical for the settlement''s long-term survival.[/p]
