INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('0f224c4a-e714-4fa2-b48a-0ea4e1380555', '33081217', 'species', '89db1f6b-0e74-471d-99f2-de999a4112d4', '2025-10-07 16:58:55.722324', '{"id": "89db1f6b-0e74-471d-99f2-de999a4112d4", "diet": "", "name": "Frost Veins", "genre": "Fantasy", "userId": "33081217", "habitat": "", "behavior": "", "imageUrl": "", "lifespan": "", "abilities": [], "createdAt": "2025-10-07T16:58:55.697Z", "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "weaknesses": [], "imageCaption": "", "intelligence": "", "reproduction": "", "articleContent": null, "classification": "Plants", "culturalTraits": "", "socialStructure": "", "physicalDescription": "In high altitudes of snow and stone in The Rocky Mountains, the Verdancy has learned new cruelties that are better suited to the thin air and bitter cold temperatures. In these areas where The Archive Collective once believed that humanity might find sanctuary, evolution writes its adaptations in ice and shadows.\n\n\n\n\n\nThe Frost Veins themselves have spawned their own ecosystem of parasitic flora. They thread through the snow like bioluminescent capillaries that pulse with a rhythm that mimics hibernating heartbeats, drawing heat from any source to trigger catastrophic bloom events. The Frost Veins respond to the Verdant-shard pendant with increasing intensity, their pulsing synchronizing with Juniper Corvain''s heartbeat until she is able to feel the entire mountain''s root network as an extension of her own nervous system. But the longer she remains in contact with the Frost Vein network, the more she feels her own body temperature dropping, her metabolism slowing to match the hibernation rhythms of the high-altitude adaptations."}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');
