INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('729d0c2b-1295-412b-95d6-28962eff7943', '33081217', 'item', 'b57e618c-b0b3-4fb7-9cb8-6b02fd015b66', '2025-10-07 16:58:56.354917', '{"id": "b57e618c-b0b3-4fb7-9cb8-6b02fd015b66", "name": "Gritstone Cakes", "genre": "Fantasy", "value": "", "rarity": "", "userId": "33081217", "weight": "", "history": "", "crafting": "", "imageUrl": "", "itemType": "Food", "abilities": [], "createdAt": "2025-10-07T16:58:56.331Z", "materials": [], "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "properties": [], "description": "Gritstone cakes are the Saltfolk''s durable, portable survival food. It is made using Mesquite Pinole and Cattails root flour that is mixed with just enough water to form a very stiff dough. It is shaped into thick, biscuit-like patties and baked on a hot, flat stone (gritstone) that is placed next to the fire. The result is a dense, dry, and incredibly durable cake that lasts for weeks without spoiling, making it the perfect travelling food for hunters, scouts, and", "imageCaption": "", "requirements": "", "articleContent": null}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');
