INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('0ae716cb-6914-42c3-86a8-b351e319ca28', '33081217', 'item', 'cb46762c-f03b-4871-aef7-c39c1aaedaaf', '2025-10-07 16:58:58.624542', '{"id": "cb46762c-f03b-4871-aef7-c39c1aaedaaf", "name": "Pinyon Nut Butter", "genre": "Fantasy", "value": "", "rarity": "", "userId": "33081217", "weight": "", "history": "", "crafting": "", "imageUrl": "", "itemType": "Consumable, Food / Drink", "abilities": [], "createdAt": "2025-10-07T16:58:58.600Z", "materials": [], "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "properties": [], "description": "The nuts of the Pinyon Pine Tree are ground into a rich, oily paste or butter—a nutrient-dense superfood that is easily transported back to Espera during a Piñonada.", "imageCaption": "", "requirements": "", "articleContent": null}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');
