INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('5413704b-0488-4c04-be64-32bfaeade32c', '33081217', 'profession', '26ee4527-79ec-4966-80e2-26993f4b80b8', '2025-10-07 16:58:59.258232', '{"id": "26ee4527-79ec-4966-80e2-26993f4b80b8", "name": "Saltbreakers", "genre": "Fantasy", "userId": "33081217", "createdAt": "2025-10-07T16:58:59.234Z", "riskLevel": "", "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "commonTools": [], "description": "The saltbreakers are Espera‘s mining crew that work in the salt mines to harvest salt, potash, and gypsum.", "seasonalWork": false, "socialStatus": "", "averageIncome": "", "mentalDemands": "", "apprenticeship": "", "professionType": "Raw Materials Gathering", "skillsRequired": [], "physicalDemands": "", "workEnvironment": "", "responsibilities": "", "trainingRequired": "", "careerProgression": "", "historicalContext": "", "relatedProfessions": [], "guildsOrganizations": [], "culturalSignificance": ""}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');
INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('e8cc8ddd-9a4b-4b5a-ac7e-2eb65134f5bf', '33081217', 'item', '5d5f1853-bdc6-4dd2-9d2e-ee57937add75', '2025-10-07 16:58:59.306796', '{"id": "5d5f1853-bdc6-4dd2-9d2e-ee57937add75", "name": "Salted Porridge", "genre": "Fantasy", "value": "", "rarity": "", "userId": "33081217", "weight": "", "history": "", "crafting": "", "imageUrl": "", "itemType": "Food", "abilities": [], "createdAt": "2025-10-07T16:58:59.282Z", "materials": [], "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "properties": [], "description": "The most common daily meal in Espera is salted porridge. It''s simply, hydrating, and provides slow-burning energy throughout the day. It is made using Mesquite Pinole that is mixed with water and Pickleweed salt in a scavenged pot or carved stone bowl. It can be eaten cold as a simple gruel, or heated into a porridge by cooking it over a stove or fire. For a richer version, the Saltfolk add some mashed dates for sweetness and a spoonful of Pinyon Nut Butter for added fat and calories.", "imageCaption": "", "requirements": "", "articleContent": null}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');
INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('c22c6ccb-0c8a-4494-82d0-49beb9cd3204', '33081217', 'ethnicity', '9ffe4c3d-98ca-4d81-aa17-78716ef4a792', '2025-10-07 16:58:59.35966', '{"id": "9ffe4c3d-98ca-4d81-aa17-78716ef4a792", "name": "Saltfolk", "genre": "Fantasy", "origin": "", "userId": "33081217", "values": [], "customs": ["The Saltfolk would have built traditions around the rhythm of survival: water", "salt", "storms", "death", "and the rare moments of abundance. Every custom", "whether sacred or playful", "grows from these constants. The Saltfolk’s traditions revolve around scarcity transformed into ritual — rain", "salt", "storms", "sheep hunts", "unions", "and deaths. Their festivals are brief but potent", "moments of collective endurance turned into joy or solemnity. Superstitions thread daily life", "keeping survival infused with meaning", "Everyday Rituals & Customs", "• Salt Circles: Drawn around cradles", "sickbeds", "or doorways to ward against Verdancy. Breaking a salt circle is a grave taboo.", "• Water Etiquette: Pouring out a few drops before drinking — an offering to the desert.", "• Morning Dew-Rite: Children collect dew from tarps at dawn. Before drinking", "they tap the gourd twice in thanks — a superstition that the desert “keeps giving.”", "• Shadow-Names: A second", "secret name whispered only to lovers or kin. Sharing it is an act of trust.", "• Salt-Palm Apologies: Offering a pinch of salt in an open hand to make peace.", "Major Traditions & Festivals", "The First Rains (Feast of the Wells)", "• Celebrated the first time each year when rainwater fills the cisterns.", "• Everyone gathers at the central well; elders bless the water with salt.", "• A communal feast follows — usually dried stores reconstituted into a large stew", "sometimes sweetened with prickly pear syrup if available.", "• Children run barefoot through the salt mud — a rare indulgence.", "The Ram Feast (Bighorn Hunt)", "• Happens whenever a hunting party returns with a sheep.", "• The whole community eats together; hunters retell the dangers of the trek.", "• Children receive the first bites of liver and marrow for strength.", "• Horns are displayed in communal halls; sometimes carved into ceremonial horns blown at unions", "council meetings", "births or deaths/funerals.", "Day of Silence (Storm Remembrance)", "• Once a year", "the Saltfolk honour those lost to storms.", "• All speech ceases from dawn until the first stars. People communicate only with hand signs and gestures.", "• At dusk", "names of the dead are carved into salt blocks", "then left to erode in the wind — symbolizing return to the desert.", "The Union Rite (Water Binding)", "• Romantic unions are marked by pouring water into a shared vessel and drinking together inside a salt circle.", "• A communal feast follows", "where families contribute small portions from their personal stores as a gesture and offering of prosperity and abundance.", "• A superstition: if the water vessel cracks during the rite", "the union will not endure.", "The Salt Harvest Festival", "• When a new vein of salt is mined or the flats yield a good season", "Saltfolk celebrate by lining the settlement’s paths with glowing salt-crystal lanterns.", "• Songs are sung — chants in English", "Spanish", "or Portuguese fragments.", "• Children are allowed to carve small salt figurines", "which are then dissolved in water the next morning — teaching the impermanence of abundance in the desert.", "Superstitions & Beliefs", "• Never Waste a Drop: Spilling water on purpose brings misfortune; accidental spills require scattering salt to “balance the loss.”", "• Stormborn Children: Babies born during a dust storm are thought to have “storm-sight” — an uncanny awareness or foresight.", "• Hollow Eyes: If someone’s eyes go pale or “glasslike", "” people whisper they are being claimed by the Verdancy. Protective salt smearing is performed.", "• Salt Dreams: Some Saltfolk believe dreams of the sea are omens of death — because water in abundance belongs only to the dead.", "• Numbers: Threes are lucky (trinity of salt", "water", "endurance)", "while sevens are avoided — thought to represent the “creeping hunger” of the Verdancy.", "Other Specific Customs", "• Birth: Newborns are anointed with a smear of brine across the forehead", "“marking them as children of scarcity.”", "• Death: The dead are salted before burial or left to desiccate naturally in salt caverns. A ritual spill of water accompanies funerals.", "• Coming of Age: At age thirteen", "a youth must survive a night alone in the salt flats with only a gourd of water and a salt pouch. Returning marks entry into adulthood.", "• Trade Days: When caravans arrive", "stalls spring up with roasted insects", "prickly pear skewers", "and embroidered cloth patches. These days are half-market", "half-festival", "often accompanied by music and storytelling."], "history": "", "imageUrl": "", "language": "The Saltfolk’s speech is a layered survival dialect, shaped by Espera’s history as a refugee settlement, its geography near the southern border, and the constant drift of traders and migrants. Language here is not just communication — it’s a survival tool and a cultural anchor.\n\n\n\n\n\nCore Language: Broken English (Salt Cant)\n\n\n\n• English is the backbone, but it’s worn down: clipped, pragmatic, stripped of unnecessary flourishes. This “Salt Cant” favors short syllables and practical terms that can be shouted over wind and sand.\n• Trade-born slang fills gaps. Saltfolk speech is littered with words from barter culture (Old World tech, medicinal jargon, Rootbound curses, etc.).\n• Harsh desert life favours metaphors from salt, storms, hunger, and thirst. Example: “He’s brined” (meaning toughened), “her eyes stormed” (meaning angry).\n\n\n\n\nRefugee Influence\n\n\nSpanish and Portuguese words filter into the Saltfolk dialect, especially given Espera’s location in the U.S. Southwest and the collapse of São Paulo early in the Verdancy timeline. Refugees carried fragments of their tongues into Espera.\n\n\n\n• Spanish influence: water terms (agua, pozo, sal, viento), family (madre, abuela), exclamations (¡Dios!, carajo), curses.\n• Portuguese influence: survival words (fome – hunger, sede – thirst, saudade – longing for what’s gone), affectionate diminutives (-inho, -inha).\n• Some refugee surnames survive intact, others blend into Espera’s naming system (e.g., Silva → Sil, Duarte → Duar, Santos → Sant).\n\nThese influences vary by quarter of Espera — areas where specific refugee groups settled. One alley might be heavy with Spanish borrowings, another with Portuguese cadences, another with clipped English salted with slang.\n\n\n\n\n\nRegional Variations\n\n\n\n• Central Espera (the salt flats core): Most stripped-down “Salt Cant,” minimal words, direct and harsh.\n• Refugee Quarters: More loanwords from Spanish and Portuguese survive here. Songs, prayers, and family talk often use these tongues more fully, especially among older generations.\n• Caravan Speech: Traders blend Salt Cant with fragments of other dialects from the wastes. They often act as linguistic bridges, spreading slang across settlements.\n\n\n\n\nRitual & Secret Languages\n\n\n\n• Whispered Prayers: Some Saltfolk still whisper prayers in Spanish or Portuguese, even if they don’t fully understand the words. These carry a weight of longing and continuity.\n• Shadow-names: Sometimes preserved in Old World tongues — a secret name in Portuguese might signal intimacy, even if daily speech is in Salt Cant.\n\n\n\n\nCultural Attitudes\n\n\n\n• Saltfolk value clarity and brevity. Someone speaking too flowery, too long-winded, is distrusted. Language must cut like salt — short, sharp, stinging.\n• But at the same time, there’s a deep reverence for song and story. Around fires, older tongues are brought out in full — Portuguese lullabies, Spanish ballads, fragments of English hymns. Language becomes a way to remember what was lost.", "religion": "", "createdAt": "2025-10-07T16:58:59.331Z", "geography": "", "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "traditions": ["The Saltfolk would have built traditions around the rhythm of survival: water", "salt", "storms", "death", "and the rare moments of abundance. Every custom", "whether sacred or playful", "grows from these constants. The Saltfolk’s traditions revolve around scarcity transformed into ritual — rain", "salt", "storms", "sheep hunts", "unions", "and deaths. Their festivals are brief but potent", "moments of collective endurance turned into joy or solemnity. Superstitions thread daily life", "keeping survival infused with meaning", "Everyday Rituals & Customs", "• Salt Circles: Drawn around cradles", "sickbeds", "or doorways to ward against Verdancy. Breaking a salt circle is a grave taboo.", "• Water Etiquette: Pouring out a few drops before drinking — an offering to the desert.", "• Morning Dew-Rite: Children collect dew from tarps at dawn. Before drinking", "they tap the gourd twice in thanks — a superstition that the desert “keeps giving.”", "• Shadow-Names: A second", "secret name whispered only to lovers or kin. Sharing it is an act of trust.", "• Salt-Palm Apologies: Offering a pinch of salt in an open hand to make peace.", "Major Traditions & Festivals", "The First Rains (Feast of the Wells)", "• Celebrated the first time each year when rainwater fills the cisterns.", "• Everyone gathers at the central well; elders bless the water with salt.", "• A communal feast follows — usually dried stores reconstituted into a large stew", "sometimes sweetened with prickly pear syrup if available.", "• Children run barefoot through the salt mud — a rare indulgence.", "The Ram Feast (Bighorn Hunt)", "• Happens whenever a hunting party returns with a sheep.", "• The whole community eats together; hunters retell the dangers of the trek.", "• Children receive the first bites of liver and marrow for strength.", "• Horns are displayed in communal halls; sometimes carved into ceremonial horns blown at unions", "council meetings", "births or deaths/funerals.", "Day of Silence (Storm Remembrance)", "• Once a year", "the Saltfolk honour those lost to storms.", "• All speech ceases from dawn until the first stars. People communicate only with hand signs and gestures.", "• At dusk", "names of the dead are carved into salt blocks", "then left to erode in the wind — symbolizing return to the desert.", "The Union Rite (Water Binding)", "• Romantic unions are marked by pouring water into a shared vessel and drinking together inside a salt circle.", "• A communal feast follows", "where families contribute small portions from their personal stores as a gesture and offering of prosperity and abundance.", "• A superstition: if the water vessel cracks during the rite", "the union will not endure.", "The Salt Harvest Festival", "• When a new vein of salt is mined or the flats yield a good season", "Saltfolk celebrate by lining the settlement’s paths with glowing salt-crystal lanterns.", "• Songs are sung — chants in English", "Spanish", "or Portuguese fragments.", "• Children are allowed to carve small salt figurines", "which are then dissolved in water the next morning — teaching the impermanence of abundance in the desert.", "Superstitions & Beliefs", "• Never Waste a Drop: Spilling water on purpose brings misfortune; accidental spills require scattering salt to “balance the loss.”", "• Stormborn Children: Babies born during a dust storm are thought to have “storm-sight” — an uncanny awareness or foresight.", "• Hollow Eyes: If someone’s eyes go pale or “glasslike", "” people whisper they are being claimed by the Verdancy. Protective salt smearing is performed.", "• Salt Dreams: Some Saltfolk believe dreams of the sea are omens of death — because water in abundance belongs only to the dead.", "• Numbers: Threes are lucky (trinity of salt", "water", "endurance)", "while sevens are avoided — thought to represent the “creeping hunger” of the Verdancy.", "Other Specific Customs", "• Birth: Newborns are anointed with a smear of brine across the forehead", "“marking them as children of scarcity.”", "• Death: The dead are salted before burial or left to desiccate naturally in salt caverns. A ritual spill of water accompanies funerals.", "• Coming of Age: At age thirteen", "a youth must survive a night alone in the salt flats with only a gourd of water and a salt pouch. Returning marks entry into adulthood.", "• Trade Days: When caravans arrive", "stalls spring up with roasted insects", "prickly pear skewers", "and embroidered cloth patches. These days are half-market", "half-festival", "often accompanied by music and storytelling."], "imageCaption": "", "articleContent": null, "culturalTraits": "Physical Relics\n\n\n\n• Scrap Relics: Pieces of Old World tech — radios, solar panels, glass bottles, watches — patched and repurposed until they hardly resemble their original function. These items often carry symbolic weight even if they no longer work.\n• Salt-etched Icons: Some families keep relics carved from salt blocks, etched with protective sigils. These may have started as trade markers or tools but became heirlooms.\n• Old Maps & Books: Crumbling atlases, waterlogged textbooks, hymnals, or journals survive in fragments. They’re treated as half-mythical — words deciphered, reinterpreted, and woven into folklore.\n\n\n\n\nPlaces:\n\n\n\n• The Salt Wells: Central gathering points, seen as sacred because they sustain life.\n• Collapsed Outposts: Old ruined shelters in the flats that serve as “ancestor houses” — reminders of earlier Saltfolk who endured storms.\n\n\n\n\nIntangible Inheritance\n\n\n\n• Folklore & Oral Histories: Myths of the First Saltfolk who fled Verdancy, tales of children surviving storms, parables of betrayal and endurance. These stories shape morality more than written law.\n• Songs: Fragments of Old World songs linger, but reworked — hymns stripped to a few haunting lines, Portuguese lullabies that mothers still hum, ballads that become chants during labor. Song is memory made audible.\n\n\n\n\nMyths:\n\n\n\n• The Salt Mother — a figure who withholds water but gives salt, teaching that scarcity is sacred.\n• The Hollow Man — cautionary tale of one who let Verdancy breach the flats, teaching vigilance and distrust of greed.\n• The Storm Children — born in the heart of dust storms, said to have eyes like glass; mythologizes resilience.\n\n\n\n\nTraditions:\n\n\n\n• Water Rites: Pouring shared water in unions, marking death by spilling water into salt.\n• Salt Circles: Protective boundaries traced at births, unions, and funerals.\n• Shadow-names: Secret names passed through families, meant to guard one’s spirit.\n• The Great Glass Piles: Mounds of scavenged glass near Espera, glittering in the sun, treated almost like shrines to resilience.\n\n\n\n\nImportance to Modern Generations\n\n\n\n• Deeply Important: These inheritances are not luxuries — they are survival’s memory. To abandon them feels like cutting ties with what kept Espera alive.\n• But Selective: Not all relics or traditions are equally preserved. Youth may see certain myths as superstition. Some elders cling fiercely, others adapt.\n• Living Culture: Myths and relics are constantly reinterpreted — songs evolve, traditions shift. Preservation is not about static repetition but about using the past to make sense of now.\n\n\n\n\nEfforts to Conserve vs. Erase\n\n\n\n• Conservation:\n-• Elders and storytellers guard folklore like treasure.\n-• Healers preserve old remedies, often mixed with Rootbound knowledge despite prejudice.\n-• Families pass relics to children, often with ceremony.\n• Erasure / Resistance:\n-• Some Saltfolk reject parts of the past that bring shame — names tied to betrayal, myths tied to fear of Hollowed.\n-• There are efforts to suppress certain languages (e.g., younger folk dropping Portuguese/Spanish in favor of blunt Salt Cant).\n-• Some wish to abandon relics they see as useless “Old World junk,” preferring new, pragmatic survival tools.", "physicalTraits": "", "socialStructure": ""}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');
