INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('86aae157-4109-42eb-ad85-561ec2e7a5ac', '33081217', 'species', '0ef21e26-06e0-447f-9fa8-7e54acb153d7', '2025-10-07 16:59:00.051502', '{"id": "0ef21e26-06e0-447f-9fa8-7e54acb153d7", "diet": "", "name": "Stone Moss", "genre": "Fantasy", "userId": "33081217", "habitat": "", "behavior": "", "imageUrl": "", "lifespan": "", "abilities": [], "createdAt": "2025-10-07T16:59:00.006Z", "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "weaknesses": [], "imageCaption": "", "intelligence": "", "reproduction": "", "articleContent": null, "classification": "Plants", "culturalTraits": "", "socialStructure": "", "physicalDescription": "The Stone Moss, Lithophagus persistens, has learned to digest granite. This organism spreads across alpine cliff faces in patterns that follow mineral veins, breaking down stone into digestible compounds while creating handholds and crevices that seem almost deliberately placed. It secretes a mild psychoactive compound through its surface, causing climbers to experience a false sense of security and confidence that leads them to take increasingly dangerous risks. Those who spend too long in contact with the moss find their own bones beginning to soften, calcium being leached away to feed the hungry lichen. It often dissolves rock ledges or cliff faces that seem stable, but once the moss has eaten away at the underlying structure, they collapse—sending humans to their death to be consumed by the lichen to provide essential nutrients, water, and minerals."}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');
