INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('e6347788-6684-4edd-863f-cd4e63c07dc2', '33081217', 'item', '7d9502fa-152e-4d60-8053-18c907c42625', '2025-10-07 16:59:01.811484', '{"id": "7d9502fa-152e-4d60-8053-18c907c42625", "name": "Yucca", "genre": "Fantasy", "value": "", "rarity": "Common: rocky slopes, bajadas, canyons, Lee Flat, Grapevine Mountains", "userId": "33081217", "weight": "", "history": "", "crafting": "", "imageUrl": "", "itemType": "Consumable, Food / Drink", "abilities": [], "createdAt": "2025-10-07T16:59:01.786Z", "materials": [], "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "properties": [], "description": "The dominant species of yucca in and around Death Valley, including the Mojave yucca (Yucca schidigera), the Banana yucca (Yucca baccata), and the iconic Joshua Tree (Yucca brevifolia), are primarily spring bloomers. Their flowering is dependent on elevation and rainfall, but generally occurs between February and late April.\n\n\n\n\n\nFlowers\n\n\nThe petals of yucca flowers are edible and can be eaten raw in moderation. They have a crisp texture and a mildly sweet, sometimes slightly bitter, flavour reminiscent of artichoke or green beans. Saltfolk often cook them to improve taste and reduce bitterness. Boiling or sautéing the petals is a common preparation method. Some Saltfolk experience a scratchy throat or stomach upset from eating the flowers raw due to saponins, which are easily broken down by cooking.\n\n\n\n\n\nFruit\n\n\nThe fleshy, banana-shaped fruits of the Banana yucca and the drier, capsule-like fruits of the Mojave yucca and Joshua Tree are best harvested when young and tender. The fruits can be roasted, which brings out a natural sweetness, and then eaten. They are also dried and ground into a meal for later use.\n\n\n\n\n\nStalks\n\n\nThe young, emerging flower stalks of many yucca species are harvested before they fully develop. When peeled and roasted or boiled, they have a flavour similar to asparagus.\n\n\n\n\n\nRoots and Leaves\n\n\nWhile the roots are rich in saponins, they are used to create a natural soap. The tough, fibrous leaves are not eaten, but are an essential resource for making cordage, baskets, and sandals.", "imageCaption": "", "requirements": "", "articleContent": null}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');
INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('f7fd1ea0-f655-4635-a44f-838a538d2179', '33081217', 'plant', '950fbbab-bcff-46c0-aa5b-8bd0c092f34d', '2025-10-10 15:48:15.732433', '{"id": "950fbbab-bcff-46c0-aa5b-8bd0c092f34d", "name": "Whisperbell Moss", "type": "Perennial bryophyte", "genre": null, "userId": "33081217", "habitat": "Thrives in humid, sheltered crevices of limestone cliffs and cave entrances in temperate mountain regions, typically between 3,000-6,500 feet elevation where morning mists provide consistent moisture", "imageUrl": null, "createdAt": "2025-10-10T15:48:15.639Z", "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "description": "A distinctive moss forming dense, cushion-like colonies up to 8 inches across. Its delicate stems rise 2-3 inches high, crowned with tiny bell-shaped sporangia that emit soft, musical tones when disturbed by wind or movement. The moss displays a rich emerald color in shade, shifting to bronze-gold in direct sunlight. Each individual plant consists of thread-like stems densely packed with overlapping, scale-like leaves that shimmer with microscopic droplets of moisture.", "imageCaption": null, "hardinessZone": "Zones 5-8, with protection from extreme temperature fluctuations", "articleContent": null, "bloomingSeason": "Sporangia mature in late spring through early summer, with peak resonance occurring during morning hours when humidity is highest", "scientificName": "Bryum resonantia", "characteristics": ["Produces audible resonance when air passes through sporangia", "Changes color based on light exposure", "Forms intricate geometric patterns in colony growth", "Releases spores that sparkle briefly in sunlight"], "careInstructions": "Requires constant humidity above 70%, indirect light, and excellent air circulation. Mist daily with distilled water and maintain temperatures between 50-65°F. Grows best on porous limestone or aged concrete surfaces with good drainage"}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');
INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('4c449c7c-2b72-4d2b-aaa9-b08d452204cc', '33081217', 'creature', '478114bf-9865-4db9-b3da-a67aa79f7ed2', '2025-10-10 16:01:11.88592', '{"id": "478114bf-9865-4db9-b3da-a67aa79f7ed2", "name": "Veilwhisper", "genre": null, "userId": "33081217", "habitat": "Abandoned places where memories linger - old libraries, forgotten theaters, derelict homes", "behavior": "Feeds on forgotten memories and lost moments, drawn to places where strong emotions once occurred. Hunts by creating nostalgic illusions to lure prey closer, then drains their most cherished recollections. Solitary by nature but occasionally forms temporary bonds with melancholic individuals who share their memories willingly.", "imageUrl": null, "abilities": ["Memory absorption through touch", "Phasing between physical and ethereal states", "Creating realistic illusions from absorbed memories"], "createdAt": "2025-10-10T16:01:05.472Z", "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "creatureType": "Ethereal Predator", "imageCaption": null, "articleContent": null, "physicalDescription": "A serpentine creature the size of a large cat, with translucent silver scales that shimmer like mercury. Its elongated body phases between solid and gaseous states. Six delicate, moth-like wings flutter along its spine, and its face bears large, opalescent eyes that reflect scenes from the past. Wisps of luminous mist constantly drift from its form.", "culturalSignificance": "Scholars believe encountering a Veilwhisper is both blessing and curse - it can restore lost memories to those who have forgotten loved ones, but at the cost of eventually consuming all remembrance of joy. Local folklore warns that houses visited by Veilwhispers become ''hollow places'' where happiness can never take root again."}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');
INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('6a3cac0e-ba5f-41dc-9e2c-e2990f7f16a2', '33081217', 'plant', 'e32d2fbc-9564-47a6-a625-7c99f4e288ea', '2025-10-07 16:59:01.541806', '{"id": "e32d2fbc-9564-47a6-a625-7c99f4e288ea", "diet": "", "name": "Whisper Vines", "genre": "Fantasy", "userId": "33081217", "habitat": "", "behavior": "", "imageUrl": "", "lifespan": "", "abilities": [], "createdAt": "2025-10-07T16:59:01.518Z", "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "weaknesses": [], "imageCaption": "", "intelligence": "", "reproduction": "", "articleContent": null, "classification": "Plants", "culturalTraits": "", "socialStructure": "", "physicalDescription": "Whisper Vine, Communicans arenius, grows in the abandoned salt mines where sound carries strange distances. This plant has developed a primitive neural network through its root system that is capable of transmitting chemical signals across sprawling underground networks. When it is disturbed, it releases different pheromone signatures, depending on the nature of the threat. Effectively, it creates an early warning system that spans The Badwater Basin. Their chemical communications allow the Saltfolk to detect approaching dangers days before they arrive—whether these are sandstorms, raiders, Bloom Fronts, or the Verdancy itself. However, the whisper vines respond to fear and violence by releasing increasingly potent chemical cocktails, and prolonged exposure has proven to alter human brain chemistry. Some Saltfolk develop an almost telepathic sensitivity to the plants'' warnings, but at the cost of growing detached from their own kind, until they speak more readily with roots than their neighbours."}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');
