INSERT INTO public.saved_items (id, user_id, item_type, item_id, created_at, item_data, notebook_id) VALUES ('cec5d7a1-41f2-4990-a4ed-2b86faadc575', '33081217', 'character', '51c88fef-34aa-41ab-9544-6c1df81d1842', '2025-10-07 16:58:57.826689', '{"id": "51c88fef-34aa-41ab-9544-6c1df81d1842", "age": 56, "ego": null, "sex": null, "flaw": null, "pets": null, "build": "", "debts": null, "funds": null, "genre": "Fantasy", "likes": "", "vices": null, "accent": null, "allies": null, "assets": null, "family": null, "gender": "Male", "height": "", "legacy": null, "prefix": null, "suffix": "", "userId": "33081217", "weight": "", "defects": null, "dialect": null, "enemies": null, "hobbies": null, "insults": null, "species": "5997d5e7-acf9-4c4f-bbcd-66afc204a336", "charisma": null, "dislikes": null, "eyeColor": "", "imageUrl": "/objects/uploads/176d072a-e538-46e0-aa83-edfd33b5ad3a", "nickname": "", "pronouns": "he/him", "skinTone": "", "strength": null, "swearing": null, "backstory": "", "createdAt": "2025-10-07T16:58:57.802Z", "education": "", "ethnicity": null, "etiquette": null, "farewells": null, "givenName": "Milan", "greetings": null, "hairColor": "", "hairStyle": null, "interests": null, "languages": [], "metaphors": null, "mutations": null, "strengths": "", "activities": null, "addictions": null, "conditions": null, "confidence": null, "familyName": "Langford", "maidenName": "", "mainSkills": "", "mannerisms": null, "middleName": "Anthony", "motivation": "", "notebookId": "0fe4e254-02d1-4a14-9046-6b3aefe17a89", "occupation": "333e652d-4351-49e4-b50f-ef7c9af67e5b", "profession": null, "upbringing": null, "voicePitch": null, "accessories": null, "compliments": null, "dateOfBirth": "", "dateOfDeath": "", "description": "Dr. Milan Anthony Langford is married to Sylvia Langford and father to twin boys—Silvan and Ashton. Though maybe \"father to twin boys\" isn''t quite right anymore. Not since the frost veins took Ashton.\n\nHe was always more present with his children than Sylvia was. The boys got his attention, his time, his affection. He gave Silvan an Old World pocket watch once—a real keepsake, something that mattered.\n\nSince Ashton''s death, Milan hasn''t been the same. Can''t be, really. The grief sits in him like a stone he can''t cough up. He''s thrown himself into his work with an intensity that borders on obsession, head down, hands busy, mind occupied. Anything to avoid the quiet moments when the loss creeps back in.\n\nWhat eats at him—and he''d never say this out loud—is the resentment toward Sylvia. She just kept working after Ashton died. Like nothing happened. Like they didn''t lose half of what made their family whole. He watches her bury herself in her own work and interprets it as indifference. She doesn''t care, he thinks. So why should he be the only one drowning?\n\nMaybe that''s unfair. Probably is. But grief makes people unfair.\n\nSo he works. Studies the Verdancy like it holds answers. Like if he can just understand it, cure it, stop it—then maybe he won''t lose anyone else. Maybe he can control something in a world that''s already taken too much from him.", "hairTexture": null, "investments": null, "personality": null, "toneOfVoice": null, "wealthClass": null, "workHistory": null, "catchphrases": null, "dependencies": null, "extroversion": null, "familialTies": null, "famousQuotes": "", "heightDetail": null, "hygieneValue": null, "imageCaption": "", "importSource": null, "keyEquipment": null, "mentalHealth": "", "placeOfBirth": "", "placeOfDeath": "", "rememberedBy": null, "behavingStyle": null, "commonPhrases": null, "facialDetails": null, "lackingSkills": null, "proficiencies": null, "secretBeliefs": null, "speakingStyle": null, "typicalAttire": "", "articleContent": null, "characterFlaws": "", "facialFeatures": "", "genderIdentity": "", "honorificTitle": "Dr.", "negativeEvents": null, "religiousViews": null, "worldInfluence": null, "accomplishments": null, "currentLocation": null, "leadershipGroup": null, "particularities": null, "positiveAspects": null, "religiousBelief": "", "behavioralTraits": null, "currentResidence": "", "disposableIncome": null, "frownedUponViews": null, "habitualGestures": null, "identifyingMarks": "", "importExternalId": null, "keyRelationships": null, "lackingKnowledge": null, "overseeingDomain": null, "positionDuration": null, "specialAbilities": "", "specializedItems": null, "strikingFeatures": "", "physicalCondition": "Milan''s hands are always moving. Ink-stained fingers, callused from handling instruments and specimen jars. He''s developed this nervous habit of checking his own pulse—pressing two fingers to his wrist, counting. Making sure he''s still here, still ticking along. Started doing it after Ashton. Can''t seem to stop.\n\nHe''s lean in that way people get when they forget to eat. Not intentionally—he just... doesn''t notice mealtimes anymore. Sylvia used to remind him. Now she doesn''t. Now they orbit each other like distant planets in the same cold solar system.\n\nThere''s a particular slump to his shoulders these days. Like he''s carrying something invisible but heavy. People who knew him before say he used to stand straighter. Used to smile with his whole face instead of just his mouth when politeness demanded it.\n\nHis study reeks of medicinal herbs and that sharp, mineral smell of Verdancy samples preserved in formaldehyde. He''s got notebooks everywhere—stacked on every surface, stuffed with observations about the frost veins, about infection patterns, about anything that might explain why his son had to die. The handwriting gets messier as you go through them. More desperate.\n\nHe talks to himself while he works. Not full conversations, just fragments. \"No, that''s not—\" or \"Try again\" or sometimes just Ashton''s name, barely whispered, like he''s testing whether saying it still hurts. It does.\n\nSilvan comes by sometimes, stands in the doorway waiting to be noticed. Milan looks up eventually, tries to be present, but there''s this gulf between them now. He sees Ashton in Silvan''s face—same jaw, same way of tilting his head—and it''s too much. So he looks away. Buries himself back in the work. Silvan leaves without saying what he came to say.\n\nThe pocket watch Milan gave Silvan? He bought two. The other''s in his desk drawer, still wound, still keeping time for a boy who doesn''t need it anymore.", "sexualOrientation": "", "speechImpediments": null, "intellectualTraits": "", "spiritualPractices": null, "supernaturalPowers": "", "valuesEthicsMorals": "", "genderUnderstanding": null, "physicalDescription": "", "physicalPresentation": null, "languageFluencyAccent": null, "marksPiercingsTattoos": null, "speechParticularities": null, "affiliatedOrganizations": null, "distinctiveBodyFeatures": "", "distinctPhysicalFeatures": null}', '0fe4e254-02d1-4a14-9046-6b3aefe17a89');


