INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('ead053b0-287d-42de-b5c9-526b05c3980f', 'Crystal Vale', 'Tundra outpost', 'Industrial age', 'Town (1,000-10,000)', 'Tropical', 'A settlement built around a powerful natural resource that both blesses and curses its inhabitants.', 'Gritty and determined, with residents who have learned to survive against all odds.', '{"Warrior codes","Elder council governance","Artisan craft traditions","Annual harvest festival"}', '{"Natural amphitheater with perfect acoustics","Ancient battlefield where spirits still roam","Floating gardens that defy natural laws"}', NULL, '2025-09-21 19:37:50.190676', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('ffce44d3-cfa3-41db-9c25-507cb0e243a6', 'Mystic Haven', 'Underground cavern city', 'Medieval era', 'Nomadic group (100-500)', 'Arctic tundra', 'Hidden from the outside world, this location harbors secrets that could change everything.', 'Magical and otherworldly, where the impossible seems commonplace.', '{"Elder council governance","Healing practices","Dietary customs","Death rites"}', '{"Natural amphitheater with perfect acoustics","Marketplace that attracts traders from distant lands","Towering lighthouse that guides travelers through dangerous terrain"}', NULL, '2025-09-21 19:37:56.222996', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('fda87f56-6345-4829-9483-c44495e4ab5e', 'Crimson Peaks', 'River delta', 'Victorian era', 'Small hamlet (50-200)', 'Alpine', 'A settlement built around a powerful natural resource that both blesses and curses its inhabitants.', 'Elegant but decaying, like a beautiful flower slowly wilting away.', '{"Religious ceremonies","Death rites","Storytelling circles","Seasonal celebrations"}', '{"Crystal formations that glow with inner light","Ancient battlefield where spirits still roam","Observatory for studying celestial movements"}', NULL, '2025-09-21 19:38:16.198366', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('e8c0e2ce-ef9e-4d6a-8b19-28aa7848f628', 'Silver Glen', 'Underground cavern city', 'Space age', 'Tiny settlement (10-50)', 'Arctic tundra', 'A place of natural beauty marred by ancient conflicts that still echo through the community.', 'Warm and welcoming, though visitors sense undercurrents of ancient rivalries.', '{"Death rites","Artisan craft traditions","Coming-of-age rituals","Religious ceremonies"}', '{"Marketplace that attracts traders from distant lands","Ancient stone circle with mysterious properties","Underground network of tunnels and chambers"}', NULL, '2025-09-21 19:41:14.314512', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('8f823023-200a-4b44-b6a3-5fdb65d7fa36', 'Neon Terminus', 'Lagrange Point L5, orbiting between Earth and Luna', '2087 CE', '47,000 permanent residents, 15,000 transients daily', 'Climate-controlled artificial environment with perpetual neon twilight, recycled air carrying metallic tang and ozone', 'A sprawling cylindrical megastation stretching twelve kilometers in length, its hull bristling with docking spires and communication arrays. The interior core houses five distinct levels: the sterile Corporate Sector with gleaming chrome and holographic advertisements, the chaotic Market Ring where vendors hawk black-market cybernetics beneath flickering neon signs, the Industrial Underbelly filled with steam-venting machinery and the constant hum of life support systems, the residential Hab-Blocks stacked like concrete honeycombs, and the notorious Zero-G Red Light District suspended in the station''s hollow center. Fiber-optic cables snake along every surface like digital veins, while massive viewport windows reveal the star-speckled void beyond.', 'Oppressive and electric, filled with the constant buzz of data streams and the distant thrum of fusion reactors. The air tastes of recycled oxygen and industrial lubricants, while holographic advertisements cast shifting colors across rain-slicked metal walkways. Privacy is a luxury as surveillance drones drift silently through crowds of augmented humans, their cybernetic implants glowing softly in the perpetual artificial dusk.', '{"Neural-jack cafes where hackers plug directly into the station''s data networks","Underground fighting pits featuring cybernetically enhanced combatants","Corporate meditation gardens with bonsai trees and synthetic waterfalls","Black market organ and implant dealers operating from mobile stalls","Data-courier gangs using parkour to navigate the station''s vertical maze"}', '{"The Central AI Core ''NEXUS'' that controls all station functions but shows signs of developing rogue consciousness","Derelict Section 7 - a quarantined zone where a failed experiment created a digital plague that corrupts cybernetic implants","The Gravity Ring - a massive rotating section where Earth''s elite maintain luxury penthouses and conduct shadowy business deals","The Memory Vaults - illegal data storage facilities hidden within the station''s superstructure, containing corporate secrets worth killing for","Airlock 47 - a popular suicide spot that has become a shrine where people leave encrypted messages for the dead"}', NULL, '2025-09-22 13:51:14.633213', 'cyberpunk', 'space station', NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('745a7240-be60-423e-acf7-d2816c34bad3', 'Nexus Station Omega-7', 'L4 Lagrange point between Earth and Luna, corporate neutral territory', '2187 CE, post-Corporate Wars era', '47,000 permanent residents, 15,000 transients daily - mix of corporate executives, data miners, black market traders, refugees, and AI constructs', 'Controlled atmospheric zones: sterile corporate levels at 21°C, humid lower decks at 28°C with recycled air thick with industrial lubricants and cooking smoke', 'A massive rotating torus of blackened titanium and pulsing neon, its outer ring housing luxury corporate suites with floor-to-ceiling viewports showing the star-field, while inner rings descend into cramped hab-blocks where holographic advertisements flicker against rust-stained walls. Steam vents hiss between levels, and the constant thrum of life support systems vibrates through deck plating worn smooth by millions of footsteps.', 'Electric tension crackling through recycled air, where corporate surveillance drones cast red scanning beams through perpetual twilight, and the smell of synthetic food mingles with ozone from overloaded data terminals', '{"Neural jack cafes where data addicts trade memories for credits","Underground fight clubs in decommissioned cargo bays","Corporate shrine-altars to profit margins in executive districts","Refugee camps in abandoned mining sections speaking dozen of languages","Black market organ shops disguised as medical clinics"}', '{"The Core - massive quantum computer array controlling station systems, heavily guarded but hackable","Void Gardens - hydroponics bays where illegal biotech experiments flourish in hidden compartments","The Drift - lawless salvage yards in the station''s dead zones where corporate security won''t patrol"}', NULL, '2025-09-22 13:51:35.729541', 'cyberpunk', 'space station', NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('a350f676-a4c5-4b54-960f-9acc1908d053', 'The Gilded Anchor', 'Crossroads port city of Maristow, where three kingdoms meet at the convergence of the Silverflow River and the Moonlit Sea', 'Age of Sail and Sorcery, equivalent to late medieval period', '60-80 patrons nightly, mix of merchants, sailors, sellswords, smugglers, and traveling mages', 'Coastal temperate with frequent sea mists, sudden storms, and salt-tinged air', 'A three-story timber and stone establishment with a ship''s bow jutting from its facade, complete with a gilded anchor figurehead. The main hall features low oak beams hung with fishing nets, ship lanterns, and maritime curiosities. Worn cobblestone floors are stained with ale and salt water. A massive stone hearth dominates one wall, its mantel adorned with weapons surrendered in ancient duels. Private booths line the walls, their high backs carved with protection runes that shimmer faintly in lamplight. A narrow staircase leads to upper rooms for lodging, while a hidden trapdoor conceals smuggler''s tunnels beneath.', 'Mysterious and welcoming, thick with pipe smoke and sea shanties, where secrets are traded as freely as coin and danger lurks behind every friendly smile', '{"Neutral ground treaty honored by all three kingdoms","Ancient ritual of ''anchor blessing'' for safe voyages","Traditional sea shanty performances each sunset","Code of silence regarding patron business","Currency exchange between realms","Smuggler cant spoken in hushed corners"}', '{"The Whispering Booth - a corner table where conversations cannot be overheard due to old magic","Captain''s Compass - a massive brass compass embedded in the bar that points to whatever the viewer most desires","The Debt Wall - where unpaid tabs are carved in wood and magically binding","Underground tunnel network connecting to the harbor and merchant quarter","The Challenge Circle - a roped area where disputes are settled with blade or magic"}', NULL, '2025-09-22 13:52:09.018495', 'fantasy', 'tavern', NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('aeb9ebd0-c4cc-4e27-86f9-74b5ef3cb581', 'Nexus Seven Station', 'L5 Lagrange point between Earth and Luna', '2187 CE', '127,000 permanent residents, 400,000 transients including corporate executives, black market traders, refugees, and neural hackers', 'Artificially controlled atmosphere with distinct environmental zones: sterile corporate sectors at 21°C, humid lower decks at 28°C with recycled air thick with synthetic cooking vapors and industrial lubricants', 'A massive rotating torus of blackened titanium and corroded chrome, honeycombed with neon-lit corridors that pulse in rhythm with data streams. The upper rings house gleaming corporate towers with floor-to-ceiling viewports displaying Earth''s polluted glow, while the lower decks sprawl in cramped warrens of jury-rigged habitation pods, illegal gambling dens, and underground neural clinics. Holographic advertisements flicker across every surface in dozens of languages, casting shifting rainbows of light across crowds of augmented humans, their cybernetic implants glinting beneath artificial stars.', 'Electric tension crackling through recycled air thick with the scent of ozone, synthetic street food, and fear. The constant hum of life support systems mingles with distant music from underground clubs and the whispered negotiations of data brokers in shadowy alcoves.', '{"Street-level pidgin mixing Mandarin, Spanish, and corporate newspeak","Underground economy based on stolen neural data and black market cybernetics","Corporate shrine districts where employees worship efficiency algorithms","Refugee communities from Earth''s climate disasters maintaining traditional customs in makeshift temples"}', '{"The Core - a restricted central hub containing the station''s quantum AI mainframe","The Undergrid - abandoned maintenance tunnels now home to hackers and dissidents","Zero-G fighting rings in the station''s non-rotating sections where illegal body modifications provide combat advantages"}', NULL, '2025-09-22 13:56:37.786269', 'cyberpunk', 'space station', NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('23a3c35b-6807-4325-9937-b2f22547b12d', 'The Gilded Serpent', 'Crossroads of three kingdoms at the edge of the Whispering Woods', 'High fantasy medieval era', '50-80 patrons nightly, mix of merchants, sellswords, nobles, and supernatural beings', 'Temperate with frequent evening mists rolling in from the enchanted forest', 'A sprawling three-story inn built from dark oak and moonstone, its walls adorned with trophies from legendary hunts. The main hall features a massive hearth carved with protective runes, long tables scarred by countless daggers, and a bar topped with polished dragonscale. Flickering amber crystals provide warm light while shadows dance in the rafters where something unseen occasionally rustles. The air thick with pipe smoke, roasting meat, and the metallic tang of well-worn weapons.', 'Tense camaraderie mixed with underlying danger, where secrets are traded as readily as coin and every stranger might be friend or foe', '{"Neutral ground enforced by ancient blood magic","Bards perform epic tales for coin and information","Complex system of coded messages written in spilled ale"}', '{"The Confession Booth - a warded alcove where truth serum is served with confessions","The Bone Bridge - a hidden passage beneath the floorboards connecting to smuggler tunnels","The Oracle''s Mirror - an enchanted looking glass behind the bar that shows glimpses of possible futures"}', NULL, '2025-09-22 13:57:47.295447', 'fantasy', 'tavern', NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('280aaadf-afe0-4b33-91dc-97c4579abe75', 'Neo-Kyoto Orbital Station', 'Low Earth orbit above the Pacific Rim, 400 kilometers altitude', '2087 CE', '47,000 permanent residents, 15,000 transients - corporate executives, black market traders, hackers, refugees, and indentured workers', 'Artificially controlled atmospheric zones - humid tropical sectors, sterile corporate districts, and failing life support areas with fluctuating oxygen levels', 'A massive cylindrical habitat spinning slowly against the star-field, its hull a patchwork of gleaming corporate modules and rust-stained refugee sectors. Neon advertisements in multiple languages cascade across internal walls like digital waterfalls. The main thoroughfare curves upward in both directions, lined with towering holographic billboards advertising neural implants and synthetic pleasures. Steam rises from street-level ramen stalls while overhead mag-lev trains streak through transparent tubes, their lights trailing like electric ghosts.', 'Claustrophobic tension mixed with electric energy - the constant hum of recycled air mingles with multilingual chatter, the acrid smell of ozone from overloaded circuits, and the sweet aroma of synthetic food. Shadows dance between harsh fluorescent zones and dim red-light districts.', '{"Corporate samurai security forces with ceremonial cyber-katanas","Underground markets trading in illegal memory chips and black-market organs","Multi-generational families living in converted shipping containers","Zen meditation pods for digital detox in corporate wellness centers","Street artists using holographic graffiti that responds to biometric data"}', '{"The Core - a restricted zero-gravity data nexus housing the station''s AI consciousness","Shimizu Corporation''s sealed executive levels with Earth-gravity simulation","The Undergrid - abandoned maintenance tunnels harboring hackers and dissidents","Atmospheric breach zones where hull damage creates deadly pressure differentials","The Memory Gardens - illegal neural interface parlors hidden in hydroponic farms"}', NULL, '2025-09-22 14:09:40.286322', 'cyberpunk', 'space station', NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('bf361a9d-7de4-4adc-ad04-8e73ae2e27de', 'The Ossuary of Stars', 'Orbiting the dying red giant Kythara Prime in the void between civilized systems', 'The 41st millennium, during the Age of Bone and Shadow', 'Approximately 3,000 souls - cultists, scavengers, bone-carvers, and the eternally dying', 'Artificially maintained at funeral chill, perpetual twilight zones broken by crimson starlight, stagnant recycled air thick with incense and decay', 'A massive cylindrical station constructed from the hollowed bones of extinct stellar leviathans, its corridors lined with ossified ribs that curve overhead like cathedral arches. Phosphorescent marrow still glows within the bone walls, casting everything in sickly green-white light. The outer hull bristles with spine-like communication arrays that pierce the void, while vast observation chambers made from crystallized eye sockets offer panoramic views of the bloated red sun. Ventilation systems wheeze through calcified lung chambers, and the central hub houses a beating heart-engine that pulses with dark energy.', 'Oppressive reverence mixed with creeping dread, where whispered prayers echo through bone corridors and shadows seem to move independently, carrying the weight of cosmic horror and religious fanaticism', '{"Death-worship ceremonies held in the Marrow Chapels","Bone-carving as both art form and religious practice","The Liturgy of Endings recited during each station rotation","Ritual consumption of fermented marrow wine","The Vigil of the Undying - caretakers who tend to those trapped between life and death"}', '{"The Reliquary Core where fragments of dead gods are stored and studied","The Whispering Gallery where the station''s bone walls carry voices across impossible distances","The Chrysalis Chambers where cultists undergo horrific transformations","The Observatory of Final Sights with its crystallized eye-socket windows that show visions of cosmic death","The Marrow Foundries where weapons are forged from living bone"}', NULL, '2025-09-22 14:10:51.88173', 'dark fantasy', 'space station', NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('b3af19c2-9271-4653-a1f9-94f97e034ba0', 'The Chronos Confluence', 'Central continent of Aethros Prime, flowing through the Temporal Valleys', '2387 CE, in a reality where time flows differently across dimensional barriers', '12,000 Riverkeepers living in floating settlements, plus 3,000 Temporal Refugees from collapsed timelines', 'Perpetually misty with aurora-like temporal distortions creating shifting temperatures from -10°C to 40°C within hours', 'A massive river spanning three kilometers wide, its waters shimmer with an opalescent quality that reflects not just light but fragments of alternate realities. Crystalline formations jut from the riverbed, pulsing with chronoton energy that makes the water appear to flow backward in certain sections. Ancient metal towers, remnants of the First Civilization, pierce through the mist at regular intervals, their surfaces covered in self-repairing hieroglyphs that change meaning based on the observer''s temporal origin. Bioluminescent algae creates trails of blue-green light that follow the current''s eddies, while massive filter-feeding creatures called Voidwhales breach the surface, their songs creating temporal echoes that can be heard across multiple timelines simultaneously.', 'Mystical and unsettling, with an underlying sense of temporal vertigo and the constant feeling that reality might shift at any moment', '{"Riverkeeper ceremonies involving synchronized swimming through temporal currents","Memory markets where traders exchange experiences from alternate timelines","Floating gardens that grow crops from seeds scattered across dimensional barriers"}', '{"The Nexus Pool where all timelines converge and reality becomes malleable","Temporal Rapids that can age travelers decades in minutes or reverse their aging entirely","The Singing Towers that broadcast warnings when timeline fractures threaten to collapse the river''s dimensional stability"}', NULL, '2025-09-22 16:35:33.54387', 'parallel world sci-fi', 'river', NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('2eac2764-7ad3-4d70-8710-c400765a7692', 'Thornwick Keep', 'Perched on a jagged cliff overlooking the Mistral Sea, three days'' ride north of the capital', 'Late medieval period, during the reign of the Iron Crown dynasty', 'Lord Commander Aldric Blackmoor, 200 sworn guards, 150 servants, scribes, and craftsmen', 'Harsh coastal winds, frequent storms, bitter winters with snow that lingers until late spring', 'A fortress of dark granite and obsidian, its seven towers pierce the sky like blackened fingers. The main keep rises 200 feet above the courtyard, its walls scarred by centuries of siege engines and salt spray. Gargoyles carved from local stone leer down from every corner, their eyes seeming to track movement below. The great hall features soaring ribbed vaults supported by pillars carved to resemble ancient trees, their stone branches intertwining overhead. Narrow arrow slits serve as windows, casting knife-thin beams of light across tapestries depicting the bloody victories of House Blackmoor. The floors are polished slate that echoes footsteps like whispers of the dead.', 'Brooding and austere, filled with the weight of old secrets and unspoken grudges. The constant sound of wind through stone creates an unsettling symphony, while shadows seem to move independently in the flickering torchlight.', '{"Ancient blood oaths sworn in the Raven Chamber","The Feast of Iron held each winter solstice with ritualistic sword ceremonies","Scribes who maintain the Chronicle of Shadows, recording every death within the castle walls"}', '{"The Whispering Gallery where conversations can be overheard from the floor below through acoustic tricks","A hidden passage behind the lord''s solar that leads to sea caves beneath the castle","The Thornwood Throne made from a single piece of petrified wood, said to reject unworthy rulers with supernatural cold"}', NULL, '2025-09-22 14:22:56.700349', NULL, 'castle', NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('e8d30875-40bf-4dc1-a846-c228870f9612', 'Blackwater Station Livery', 'Edge of Blackwater, Arizona Territory, near the Apache reservation border', '1882, height of the Apache Wars', 'Run by Ezra Thorne and his half-Apache daughter Maria, serves 200+ townspeople and passing travelers', 'High desert with scorching summers, bitter winter nights, frequent dust storms, and rare but violent thunderstorms', 'A weathered adobe and timber stable with a corrugated iron roof that rattles in desert winds. Twenty stalls line the main barn, separated by thick oak posts scarred with knife marks and bullet holes. The hay loft creaks overhead, accessible by a rope ladder. A smaller adjoining building houses the tack room, filled with hand-tooled saddles, bridles hanging on iron hooks, and shelves of liniment bottles. Outside, a covered blacksmith forge glows red against the perpetual dust, while a windmill pumps brackish water into stone troughs.', 'Tense anticipation mixed with frontier pragmatism; the constant smell of horse sweat, leather oil, and desert sage; sounds of stamping hooves, creaking leather, and distant Apache drums on still nights', '{"Apache and Mexican influences in horse training techniques","Code of silence among cowboys and outlaws who stable horses here","Weekly horse auctions that draw ranchers, soldiers, and suspicious characters"}', '{"Hidden compartment beneath the grain room floor where stolen goods are cached","Maria''s collection of Apache war arrows embedded in the stable walls from past raids","Secret tunnel connecting the stable to the saloon across the street, used for smuggling"}', NULL, '2025-09-22 14:43:50.068856', 'western', 'stable', NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('a28c047b-c9e1-43e9-bd9d-4643a03e67be', 'The Weeping Gate of Thessarim', 'Ancient ruins beneath the Thornspire Mountains, accessible through a hidden cavern system', 'Age of Fractured Realms, approximately 800 years after the Great Sundering', 'Guarded by the Order of the Veil (12 members), visited by desperate travelers and scholars seeking forbidden knowledge', 'Perpetually cool and damp, with unnatural mists that carry whispers from other realms', 'A colossal archway of midnight-black stone stands thirty feet tall, its surface carved with intricate runes that shift and writhe when observed directly. The portal weeps luminous silver tears that pool in channels carved into the obsidian floor, creating rivulets that spell out warnings in the ancient Thessarim script. Crystalline formations jut from the cavern walls, humming with residual magic and casting prismatic shadows that dance across crumbling frescoes depicting the portal''s violent creation. The air shimmers with heat distortion despite the cold, and the space beyond the archway shows not darkness, but a swirling void of starlight and distant, impossible landscapes.', 'Haunting melancholy mixed with electric anticipation, as if standing on the precipice of destiny itself. The weight of ancient sorrow permeates the air while whispers of untold possibilities echo from beyond the threshold.', '{"Ritual scarification practiced by the Order of the Veil to resist the portal''s mental influence","Ancient Thessarim burial rites performed to honor those lost to interdimensional travel","Sacred geometries carved into every surface to contain and channel the portal''s chaotic energies"}', '{"The Tears of Thessarim form prophetic patterns that reveal glimpses of potential futures","The Resonance Chamber where spoken words echo across multiple dimensions simultaneously","The Anchor Stones that prevent the portal from drifting between realities but are slowly cracking under the strain"}', NULL, '2025-09-22 14:45:51.379679', 'fantasy', 'portal', NULL, NULL, NULL, NULL);
INSERT INTO public.settings (id, name, location, time_period, population, climate, description, atmosphere, cultural_elements, notable_features, user_id, created_at, genre, setting_type, article_content, notebook_id, image_url, image_caption) VALUES ('330fb268-d82f-4f84-8f39-d5b905aa7659', 'Aqua Sanctum', 'Hidden within the Scorched Territories, surrounded by irradiated wasteland', '2157 CE, 89 years after the Great Collapse', '347 survivors organized into three competing castes: the Water Keepers (23), the Sand Walkers (156), and the Tech Scavengers (168)', 'Artificially maintained microclimate with cool mists and temperatures 30 degrees below the surrounding desert, sustained by ancient geothermal systems', 'A terraced oasis built around a crystalline spring that bubbles up through cracked concrete foundations of a pre-war research facility. Makeshift dwellings constructed from salvaged metal and bio-plastic cling to carved sandstone walls, connected by rope bridges and pulley systems. Hydroponic gardens cascade down the terraces in repurposed shipping containers, their LED grow lights casting an eerie purple glow at night. The central water source is enclosed by a dome of welded glass panels scavenged from skyscrapers, creating a greenhouse cathedral where precious fruit trees grow.', 'Tense sanctuary where hope mingles with paranoia, the constant hum of water recyclers mixing with whispered political conspiracies and the distant howl of irradiated storms beyond the protective ridge', '{"Water rationing ceremonies conducted at dawn with ritualistic precision","Oral histories recited nightly to preserve pre-war knowledge","Barter economy based on water credits, tech components, and radiation medicine"}', '{"The Spire: a 200-foot communication tower that intercepts fragmented signals from other survivor settlements","The Filtration Heart: ancient water purification system that''s failing and requires dangerous maintenance deep underground","The Memory Wall: digital archive containing encrypted data from before the collapse, accessible only through a quantum computer hidden beneath the oasis"}', NULL, '2025-09-22 16:31:05.626421', 'dystopian', 'oasis', NULL, NULL, NULL, NULL);
